<style lang="scss" >
    @import '../../assets/main.scss';

    .job-opport-sec__card{
    width: 100%;    
    height: 100%;
    padding: 30px 30px 40px 30px;
    border-radius: 16px;
    border: 1px solid $light-grey;
    background: #FFF;
    margin-bottom: 26px;
    }

    .job-opport-sec__card-top-wrapper{
        display: flex;
        justify-content: space-between;
        align-items: start ;
        margin-bottom: 20px;
    }
    .job-opport-sec__card-logo
    {
    margin-bottom: 20px;
        
    }

    .job-opport-sec__card-new-job{
    
    display: inline-flex;
    padding: 4px;
    justify-content: flex-end;
    align-items: center;
    border-radius: 4px;
    color: $deep-green;
    background: $fresh-mint;

    }

    .job-opport-sec__card-company{
        margin-bottom: 86px;
    }
    .job-opport-sec__card-company-name{
        font-family: $font;
        font-size: 18px;
        font-weight: 500;
        font-style: normal;
        line-height: 1.2;
        color: #242424;
        margin-bottom: 4px;
    }

    .job-opport-sec__card-company-rate-wrapper{
        display: flex;
        gap: 8px;
    }
    .job-opport-sec__card-company-rate{
        font-family: $font;
        font-size: 12px;
        font-weight: 500;
        font-style: normal;
        line-height: 1.1;
        color: $dark-green;
    }

    .job-opport-sec__card-company-rate-votes{
        font-family: $font;
        font-size: 12px;
        font-weight: 500;
        font-style: normal;
        line-height: 1.1;
        color: $main-green;
        text-decoration-line: underline
    }

    .job-opport-sec__card-speciality-wrapper{
        margin-bottom: 20px;
    }

    .job-opport-sec__card-address-wrapper{
        margin: 0;
        padding-bottom: 30px;
        border-bottom: $light-grey 1px solid;
    }

    .job-opport-sec__card-title{
        font-family: $font;
        font-size: 12px;
        font-weight: 500;
        font-style: normal;
        line-height: 1.1;
        color: $neutral-grey;
        margin-bottom: 8px;
    }

    .job-opport-sec__card-name{
        font-family: $font;
        font-size: 12px;
        font-weight: 500;
        font-style: normal;
        line-height: 1.1;
        color: $dark-green;
    }

    .job-opport-sec__card-bottom-wrapper{
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding-top: 40px;
    }

    .job-opport-sec__card-bottom-text-wrapper{
        display: flex;
        gap: 8px;
    }

    .job-opport-sec__card-bottom-title{
        font-family: $font;
        font-size: 12px;
        font-weight: 600;
        font-style: normal;
        line-height: 1.1;
        color: $dark-green;
    }

    .job-opport-sec__card-bottom-text{
        font-family: $font;
        font-size: 12px;
        font-weight: 500;
        font-style: normal;
        line-height: 1.1;
        color: $neutral-grey;
    }
</style>
